#simple function 
def mysum(a,b): #defining a function
    c=a+b                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
    return c

print("before function call")

x=300
y=400
result = mysum(x,y)
print ( result )
print("after function call")


